# Write your MySQL query statement below
SELECT D.NAME AS DEPARTMENT,E.NAME AS EMPLOYEE, E.SALARY AS SALARY FROM EMPLOYEE E,DEPARTMENT D,EMPLOYEE E2 WHERE E.SALARY<=E2.SALARY AND D.ID=E.DEPARTMENTID AND E.DEPARTMENTID=E2.DEPARTMENTID GROUP BY D.ID,E.NAME HAVING COUNT(DISTINCT E2.SALARY)<=3 ORDER BY D.NAME,E.SALARY DESC;