# Write your MySQL query statement below
SELECT ID,SUM(NUM) AS NUM FROM ((SELECT R1.REQUESTER_ID AS ID,COUNT(R1.REQUESTER_ID) AS NUM FROM REQUESTACCEPTED R1 GROUP BY R1.REQUESTER_ID) UNION ALL(SELECT R2.ACCEPTER_ID AS ID, COUNT(R2.ACCEPTER_ID) AS NUM FROM REQUESTACCEPTED R2 GROUP BY R2.ACCEPTER_ID) )AS FRIENDS GROUP BY ID ORDER BY NUM DESC LIMIT 1;