SELECT ROUND(COUNT(B.PLAYER_ID)/COUNT(A.PLAYER_ID),2) AS FRACTION FROM ACTIVITY A LEFT JOIN ACTIVITY B ON A.PLAYER_ID=B.PLAYER_ID AND DATEDIFF(B.EVENT_DATE,A.EVENT_DATE)=1 WHERE (A.PLAYER_ID,A.EVENT_DATE) IN (SELECT PLAYER_ID, MIN(EVENT_DATE) FROM ACTIVITY GROUP BY PLAYER_ID);