# Write your MySQL query statement below
SELECT S.STUDENT_ID,S.STUDENT_NAME,SB.SUBJECT_NAME,COUNT(E.STUDENT_ID) AS ATTENDED_EXAMS FROM STUDENTS S CROSS JOIN SUBJECTS SB LEFT JOIN EXAMINATIONS E ON E.SUBJECT_NAME=SB.SUBJECT_NAME AND S.STUDENT_ID=E.STUDENT_ID  GROUP BY S.STUDENT_ID,S.STUDENT_NAME,SB.SUBJECT_NAME ORDER BY S.STUDENT_ID,SB.SUBJECT_NAME;